import React, { useState } from 'react';

const ContactPage = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const onClickSendMessage = async () => {
        console.log(name, email, message);

        try {
            const response = await fetch('http://localhost:3000/CryptoInfo', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ name, email, message }),
            });

            if (response.ok) {
                const data = await response.json();
                console.log(data.message); // Log the response message from the server
            } else {
                console.error('Failed to insert data into the database');
            }
        } catch (error) {
            console.error('Error:', error);
        }

        alert(`${name}, thank you for sending us a message!`);
    };

    const validateForm = () => {
        return name.trim() !== '' && email.trim() !== '' && message.trim() !== '';
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (!validateForm()) {
            alert("Please fill out all fields before submitting.");
            return;
        }
        onClickSendMessage();
    };

    return (
        <div className="tab-content hidden">
            <p>Have questions or feedback? We'd love to hear from you!</p>
            <div id="contact-details">
                <strong>Email: </strong>
                <p>info@b1_crypto.com</p>
                <strong>Phone: </strong>
                <p>+1 (555) 123-4567</p>
            </div>
            <div className="contact-form">
                <h2>Send us a Message</h2>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="name">Your Name:</label>
                    <input type="text" id="name" name="name" value={name} onChange={(e) => setName(e.target.value)} required />
                    <label htmlFor="email">Your Email:</label>
                    <input type="email" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <label htmlFor="message">Your Message:</label>
                    <textarea rows="3" id="message" name="message" value={message} onChange={(e) => setMessage(e.target.value)} required />
                    <button type="submit" style={{ border: '1px solid black' }} className="px-4 py-2 rounded">Send Message</button>
                </form>
            </div>
        </div>
    );
};

export default Contact;
