import React, { useState } from "react";
import { ToggleTheme } from "./ToggleTheme";

// Define state to manage the currently active tab
const [activeTab, setActiveTab] = useState(0);

const tabs = ["Home", "About", "Contact", "News", "FAQ"];

const changeTab = (index) => {
    // Set the active tab to the clicked tab's index
    setActiveTab(index);
}

export const Header = () => {
  return (
    <div
      id="header"
      className="bg-gray-700 text-white p-4 flex justify-between"
    >
      <div id="desktopMenu" className="justify-start gap-4 hidden sm:flex">
        {tabs.map((tab, index) => (
          <button
            key={tab}
            className={`btn btn-secondary third ${activeTab === index ? "active" : ""}`}
            onClick={() => changeTab(index)}
          >
            {tab}
          </button>
        ))}
      </div>
      <ToggleTheme />
    </div>
  );
};
